INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES ('Mass Effect Legendary Edition', 4.8, 2021, 'Role-playing (RPG), Shooter', 'XBox, Playstation, PC', 'https://shared.cloudflare.steamstatic.com/store_item_assets/steam/apps/1328670/header.jpg?t=1725576882', 'Uma épica saga espacial.', 'Explore a galáxia enquanto lidera o Comandante Shepard em uma jornada para salvar a humanidade de uma ameaça extraterrestre iminente.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES ('Red Dead Redemption 2', 4.7, 2018, 'Role-playing (RPG), Aventura', 'XBox, Playstation, PC', 'https://shared.cloudflare.steamstatic.com/store_item_assets/steam/apps/1174180/header.jpg?t=1720558643', 'Um mundo aberto no Velho Oeste.', 'Viva a história de Arthur Morgan, um fora da lei tentando sobreviver e manter seu bando unido enquanto é perseguido por autoridades e inimigos.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES ('The Witcher 3: Wild Hunt', 4.7, 2014, 'Role-playing (RPG), Aventura', 'XBox, Playstation, PC', 'https://shared.fastly.steamstatic.com/store_item_assets/steam/apps/292030/header.jpg?t=1736424367', 'A jornada de Geralt de Rívia.', 'Assuma o papel de Geralt, um caçador de monstros, e explore um vasto mundo em busca de sua filha adotiva enquanto enfrenta escolhas complexas e desafios.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES ('Sekiro: Shadows Die Twice', 3.8, 2019, 'Role-playing (RPG), Aventura', 'XBox, Playstation, PC', 'https://gepig.com/game_cover_460w/5605.jpg', 'Intenso combate samurai.', 'Aventure-se no Japão feudal como um guerreiro shinobi, enfrentando inimigos brutais e descobrindo segredos de seu passado.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES ('Ghost of Tsushima', 4.6, 2020, 'Role-playing (RPG), Aventura', 'Playstation', 'https://gepig.com/game_cover_460w/7200.jpg', 'Lute como um samurai em Tsushima.', 'Defenda sua terra natal contra invasores mongóis enquanto descobre o que significa se tornar um guerreiro fantasma.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES ('Super Mario World', 4.7, 1990, 'Platforma', 'Super Nintendo, PC', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSmCXh8WjuAHMbPIrqFHBAM9DbniM99amRZ0g&s', 'Um clássico do Mario.', 'Junte-se a Mario e Luigi em uma aventura para salvar a Princesa Peach e derrotar Bowser em um mundo cheio de diversão e desafios.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES ('Hollow Knight', 4.6, 2017, 'Platform', 'XBox, Playstation, PC', 'https://shared.fastly.steamstatic.com/store_item_assets/steam/apps/367520/header.jpg?t=1695270428', 'Uma jornada no submundo.', 'Explore um reino subterrâneo cheio de mistérios e perigos, enfrentando inimigos e descobrindo segredos escondidos.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES ('Ori and the Blind Forest', 4, 2015, 'Platforma', 'XBox, Playstation, PC', 'https://shared.fastly.steamstatic.com/store_item_assets/steam/apps/387290/header.jpg?t=1701967651', 'Uma história comovente.', 'Acompanhe Ori em uma jornada emocional através de uma floresta encantada, enfrentando desafios e perigos para salvar seu lar.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES ('Cuphead', 4.6, 2017, 'Platforma', 'XBox, Playstation, PC', 'https://shared.cloudflare.steamstatic.com/store_item_assets/steam/apps/268910/header.jpg?t=1709068852', 'Desafios em estilo retrô.', 'Entre em batalhas intensas em um mundo inspirado em desenhos animados clássicos, com desafios únicos e uma trilha sonora vibrante.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES ('Sonic CD', 4, 1993, 'Platforma', 'Sega CD, PC', 'https://shared.cloudflare.steamstatic.com/store_item_assets/steam/apps/200940/header.jpg?t=1653084020', 'Corrida no tempo com Sonic.', 'Acompanhe Sonic enquanto ele corre pelo tempo para salvar Amy e derrotar o maligno Dr. Robotnik em uma aventura inesquecível.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES ('Call of Duty: Modern Warfare', 4.8, 2019, 'FPS - Tiro em primeira pessoa', 'PC, Xbox One, PS4', 'https://steamcdn-a.akamaihd.net/steam/apps/2000950/header.jpg', 'Ação intensa e realista em combates modernos.', 'Mergulhe em missões de alta tensão e enfrente desafios em cenários realistas que capturam a essência dos combates contemporâneos.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES ('Battlefield V', 4.5, 2018, 'FPS - Tiro em primeira pessoa', 'PC, Xbox One, PS4', 'https://steamcdn-a.akamaihd.net/steam/apps/1238810/header.jpg', 'Combate em larga escala na Segunda Guerra Mundial.', 'Experimente a intensidade dos combates históricos com cenários detalhados e missões colaborativas que reconstroem momentos icônicos da Segunda Guerra Mundial.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('DOOM Eternal', 4.7, 2020, 'FPS - Tiro em primeira pessoa', 'PC, Xbox One, PS4, Nintendo Switch', 'https://steamcdn-a.akamaihd.net/steam/apps/782330/header.jpg', 'Ação frenética contra hordas demoníacas.', 'Encare desafios infernais em um jogo de tiro de ritmo acelerado, com gráficos intensos e uma trilha sonora pulsante.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('Fortnite', 4.0, 2017, 'Battle Royale', 'PC, Xbox, PlayStation, Nintendo Switch, Mobile', 'https://cdn2.steamgriddb.com/grid/f5aeecbffe87c62489add19712de0d7e.png', 'Construção e combate em um campo de batalha em constante diminuição.', 'Fortnite combina construção criativa e ação intensa em partidas online onde 100 jogadores lutam pela sobrevivência.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('Apex Legends', 4.5, 2019, 'Battle Royale', 'PC, Xbox, PlayStation', 'https://steamcdn-a.akamaihd.net/steam/apps/1172470/header.jpg', 'Equipe-se e lute para ser o último esquadrão em pé.', 'Apex Legends traz uma abordagem dinâmica ao gênero battle royale com personagens únicos e habilidades especiais que se complementam em equipes.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('PUBG', 4.3, 2017, 'Battle Royale', 'PC, Xbox One, PlayStation 4, Mobile', 'https://steamcdn-a.akamaihd.net/steam/apps/578080/header.jpg', 'Tático e realista, o clássico battle royale.', 'PUBG desafia os jogadores a sobreviver em um campo de batalha realista, onde estratégia e precisão são essenciais para a vitória.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('League of Legends', 4.6, 2009, 'MOBA - Multiplayer Online Battle Arena', 'PC', 'https://cdn2.steamgriddb.com/grid/bf580a708c65c24be111c367feee1ac5.png', 'Estratégia competitiva 5v5 em batalhas em equipe.', 'League of Legends oferece partidas intensas onde estratégia, trabalho em equipe e mecânicas refinadas determinam o vencedor em partidas altamente competitivas.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('Dota 2', 4.7, 2013, 'MOBA - Multiplayer Online Battle Arena', 'PC', 'https://steamcdn-a.akamaihd.net/steam/apps/570/header.jpg', 'Um dos MOBAs mais complexos e táticos.', 'Dota 2 é conhecido por sua profundidade estratégica e alta complexidade, onde cada decisão e sinergia em equipe podem virar o jogo.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('Heroes of the Storm', 4.0, 2015, 'MOBA - Multiplayer Online Battle Arena', 'PC', 'https://img.opencritic.com/game/884/rnA4pErd.jpg', 'Reúna heróis de vários universos da Blizzard para batalhas épicas.', 'Heroes of the Storm reúne personagens icônicos de Blizzard em partidas curtas e dinâmicas, onde a estratégia e a colaboração decidem a vitória.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('The Sims 4', 4.2, 2014, 'Simulação', 'PC, Mac, Console', 'https://steamcdn-a.akamaihd.net/steam/apps/1222670/header.jpg', 'Viva a experiência de simular a vida virtual.', 'The Sims 4 permite criar e gerenciar a vida dos personagens em um mundo virtual cheio de possibilidades criativas e interações sociais.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('Flight Simulator 2020', 4.8, 2020, 'Simulação', 'PC', 'https://steamcdn-a.akamaihd.net/steam/apps/1250410/header.jpg', 'Experimente a sensação de pilotar aviões modernos.', 'Flight Simulator 2020 oferece gráficos realistas, clima dinâmico e uma experiência de voo imersiva, simulando a aviação com precisão.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('Euro Truck Simulator 2', 4.5, 2012, 'Simulação', 'PC', 'https://steamcdn-a.akamaihd.net/steam/apps/227300/header.jpg', 'Assuma o volante de caminhões e explore a Europa.', 'Euro Truck Simulator 2 oferece uma experiência relaxante e realista de dirigir caminhões, com mapas detalhados e uma comunidade ativa.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('FIFA 23', 4.6, 2022, 'Esportes', 'PC, Xbox, PlayStation', 'https://steamcdn-a.akamaihd.net/steam/apps/1811260/header.jpg', 'O futebol em sua melhor forma.', 'FIFA 23 traz gráficos aprimorados, jogabilidade realista e modos de jogo inovadores que capturam a emoção dos campos de futebol.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('NBA 2K23', 4.4, 2022, 'Esportes', 'PC, Xbox, PlayStation', 'https://steamcdn-a.akamaihd.net/steam/apps/1919590/header.jpg', 'A experiência definitiva do basquete.', 'NBA 2K23 oferece jogabilidade fluida, realismo impressionante e uma imersão profunda no universo do basquete profissional.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('Madden NFL 23', 4.3, 2022, 'Esportes', 'PC, Xbox, PlayStation', 'https://steamcdn-a.akamaihd.net/steam/apps/1760250/header.jpg', 'Domine o futebol americano virtual.', 'Madden NFL 23 apresenta estratégias autênticas, gráficos de ponta e mecânicas de jogo que aproximam os fãs do futebol americano da ação dos campos.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('Civilization VI', 4.5, 2016, 'Estratégia', 'PC, iOS', 'https://steamcdn-a.akamaihd.net/steam/apps/289070/header.jpg', 'Construa e expanda seu império ao longo dos séculos.', 'Civilization VI desafia os jogadores a liderar uma nação desde a antiguidade até a era moderna, equilibrando diplomacia, guerra e desenvolvimento cultural.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('Age of Empires IV', 4.6, 2021, 'Estratégia', 'PC', 'https://steamcdn-a.akamaihd.net/steam/apps/1466860/header.jpg', 'Guie sua civilização através dos tempos.', 'Age of Empires IV combina estratégia em tempo real com uma profunda narrativa histórica, proporcionando batalhas épicas e decisões estratégicas complexas.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('StarCraft II', 4.7, 2010, 'Estratégia', 'PC', 'https://cdn2.steamgriddb.com/grid/5c8cb735a1ce65dac514233cbd5576d6.png', 'Conquiste o universo com estratégias futuristas.', 'StarCraft II é um clássico dos jogos de estratégia, onde diferentes raças competem em lutas intensas e táticas sofisticadas, exigindo rapidez e precisão nos comandos.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('Tekken 7', 4.3, 2015, 'Luta', 'PC, Xbox, PlayStation', 'https://steamcdn-a.akamaihd.net/steam/apps/389730/header.jpg', 'Combates intensos com personagens icônicos.', 'Tekken 7 oferece uma jogabilidade refinada e um elenco diversificado de lutadores, proporcionando lutas rápidas e cheias de estratégia.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('Street Fighter V', 4.1, 2016, 'Luta', 'PC, PlayStation', 'https://steamcdn-a.akamaihd.net/steam/apps/310950/header.jpg', 'Clássico do gênero, com batalhas icônicas.', 'Street Fighter V mantém o legado da franquia com combinações técnicas e batalhas intensas que desafiam os reflexos e a estratégia dos jogadores.');
INSERT INTO tb_game (title, score, game_year, genre, platform, img_url, short_description, long_description) VALUES('Super Smash Bros. Ultimate', 4.8, 2018, 'Luta', 'Nintendo Switch', 'https://www.hrkgame.com/media/games/.thumbnails/supersmashufpv2_cover.jpg/supersmashufpv2_cover-460x215.jpg', 'O encontro definitivo dos universos dos videogames.', 'Super Smash Bros. Ultimate reúne personagens de múltiplas franquias em batalhas caóticas e divertidas, onde habilidade, estratégia e diversão se encontram.');
